(function($){$.fn.hwScaleAndZoom=function(container){this.children('img').each(function(){var contW=$(this).parent().width();var origW=$(this).width();$(this).attr('originalwidth',origW);if(origW>contW){$(this).width(contW);$(this).css('cursor','se-resize');}});this.children('img').click(function(event){var contW=$(this).parent().width();var imgorigW=$(this).attr('originalwidth');if(imgorigW>=contW){if($(this).width()<=contW){$(this).animate({width:imgorigW},400);$(this).css('cursor','nw-resize');}
else{$(this).animate({width:contW},400);$(this).css('cursor','se-resize');}}})};})(jQuery);